record.field:
sale_obj = env['sale.order'].search([('name', '=', record.origin)], limit=1)
if sale_obj:
  for line in sale_obj.order_line:
    record.write({'checklist_line_ids': [(0, 0, {'minute_point_id': line.minute_point_id.id})]})

